$.fn.upload = function(){ 
    var $wrap = $(this),
    zCount = parseInt($wrap.attr("data-size")),  //最多上传文件数
    domName = $wrap.attr("data-dom"),  
    server = $wrap.attr("data-url"),  //上传地址
    fileLimtSize = $wrap.attr("data-filesize"),  //单个文件最大SIZE  单位K
    filetype = $wrap.attr("data-type"),  //允许上传文件的后缀
    $queue = $wrap.find('.filelist'), 
    $placeholder = $wrap.find('.placeholder'),
    $smallBtn = $('#smallFilePicker_'+domName),
    $upDataList = $wrap.find("input[name='"+domName+"']"), 
    // 添加的文件数量
    fileCount = 0,
    // 添加的文件总大小
    fileSize = 0,
    // 优化retina, 在retina下这个值是2
    ratio = window.devicePixelRatio || 1, 
    uWisth = $queue.width() - 12,
    hang = parseInt(uWisth / 118),
    // 缩略图大小
    thumbnailWidth = 110 * ratio,
    thumbnailHeight = 110 * ratio,
    _moveDom,_move

    //最大上传文件数 
    // WebUploader实例 
    if ( !WebUploader.Uploader.support() ) {
        alert( 'Web Uploader 不支持您的浏览器！如果你使用的是IE浏览器，请尝试升级 flash 播放器');
        throw new Error( 'WebUploader does not support the browser you are using.' );
    }
    if(!server) server = 'http://vip.jczs.com/File/uploadImg'
    //初始化 
    var imgList = $upDataList.val();
    if(imgList)
    {
    	imgList = imgList.split(",");
    
	    for (var i = 0; i < imgList.length; i++) {  
	        var imgListTemp = '/Uploads/'+imgList[i]; 
	        imgListTemp = imgListTemp.replace("img/",'thumb/');
	        var $li = $(
	                '<li  class="file-item thumbnail center-block">' +
	                    '<img ondrag="return false" draggable="false" src="'+imgListTemp+'">' +
	                    '<div class="info" ></div>' +
	                '</li>'
	            ) 
	        $li.insertBefore($smallBtn).data('url',imgList[i])
	    };
    }
    // 实例化 
    var uploader = WebUploader.create({
        pick: {
            id: '#uploadfilePicker_'+domName,
            label: '点击选择需要上传的文件'
        },
        fileVal: 'uploadFile',
        dnd: '#upload_'+domName+' .queueList', 

        accept: {
            title: 'Images',
            extensions: filetype ? filetype : 'gif,jpg,jpeg,bmp,png',
            mimeTypes: 'image/*'
        },
        // swf文件路径
        swf: '/index/Member/Static/plugins/webuploader/Uploader.swf',
        //disableGlobalDnd: true,
        auto : true,
        chunked: true, 
        server: server,
        fileNumLimit: zCount,
        fileSizeLimit: 100 * 1024 * 1024,     
        fileSingleSizeLimit: fileLimtSize ? fileLimtSize * 1024 : 3 * 1024 * 1024    // 50 M
    });  
    // 添加“添加文件”的按钮，
    if(zCount === 1)
    {
        uploader.addButton({
            id: '#oneFilePicker_'+domName,
            label: ''
        }); 
    }else
    {
        uploader.addButton({
            id: '#smallFilePicker_'+domName,
            label: '<i class="fa fa-folder-open"></i>'
        }); 
    }
    
    checkImgCount();
    uploader.on( 'beforeFileQueued', function( file ) {
        if(fileCount >= zCount && zCount === 1) 
        {                 
            var tempFile = $queue.find("li").data('file'); 
            if(tempFile) uploader.removeFile( tempFile );                    
            $queue.find("li").remove();
            fileCount = 0; 
        }
    })
    uploader.on( 'fileQueued', function( file ) {  
        fileCount++  
        var $li = $(
                '<li id="' + file.id + '" class="file-item thumbnail">' +
                    '<img ondrag="return false" draggable="false">' +
                    '<div class="info uping" title="'+file.name+'">正在上传</div>' +
                '</li>'
        ),
        $img = $li.find('img'); 
        $li.insertBefore($smallBtn).data('file',file)

        // 创建缩略图
        // 如果为非图片文件，可以不用调用此方法。
        // thumbnailWidth x thumbnailHeight 为 100 x 100
        uploader.makeThumb( file, function( error, src ) {
            if ( error ) {
                $img.replaceWith('<span>'+file.name+'</span>');
                return;
            }
            $img.attr( 'src', src );
        }, thumbnailWidth, thumbnailHeight ); 
        checkImgCount();
    });
    uploader.on( 'error', function( code ){ 
        if(code == "F_EXCEED_SIZE") code = "对不起，单个文件不能大于"+ (fileLimtSize ? fileLimtSize : 3*1024) +"KB！";
        if(code == "Q_TYPE_DENIED") code = "请上传" + (filetype ? filetype : 'gif,jpg,jpeg,bmp,png') + "类型的文件!"
        if(code == "Q_EXCEED_NUM_LIMIT") code = "对不起，当前表单最多允许上传"+zCount+"个文件!" 
        $('<div class="alert alert-danger bounceIn animated alert-dismissable"><button aria-hidden="true" data-dismiss="alert" class="close" type="button">×</button>'+code+'</div>').appendTo($wrap).fadeOut(13000,function(){$(this).remove()})
    }) 
    
    //拖动排序
    $queue.on("mousedown","li",function(e)//拖动排序
    { 
         _moveDom = $(this)
         if(_moveDom.hasClass("thumbnail") && !_moveDom.find(".info").hasClass("uping")){
             _move=true;             
             var offset = _moveDom.offset()
             var thisOffset = $queue.offset() 
             var left = offset.left;
             var top = offset.top;  
             _x=parseInt(left) + (e.pageX - parseInt(left)) + (thisOffset.left - offset.left);
             _y=parseInt(top) + (e.pageY - parseInt(top)) + (thisOffset.top - offset.top);
         }
    })
 
    $(document).mousemove(function(e){
        if(_move){
            var x=e.pageX-_x;
            var y=e.pageY-_y;
            var thisI = _moveDom.index()
            var offset = $queue.offset() 
            var qh = $queue.height() ? $queue.height() : 134
            _moveDom.css({top:y,left:x,zIndex:999,position:'absolute'}); 
            inX = e.pageX - offset.left
            inY = e.pageY - offset.top  
            if(inY > qh  || inY < 0)
            {
                _moveDom.addClass('del')
            }else
            {
                _moveDom.removeClass('del')
            }
            inX = parseInt(inX / 118)
            inY = parseInt(inY / 118)
            inX = inX + hang * inY
            if(inX >= thisI) inX++ 
            $queue.children("li").not(_moveDom).removeAttr("style")
            $queue.children("li").eq(inX).not(_moveDom).css("margin-left",118)

        }
    }) 
   $queue.on("mouseup","li",function(){
        if(_move){
            _move=false;
            if(_moveDom.hasClass("del"))
            {
                file = _moveDom.data('file'); 
                if(file) uploader.removeFile( file );
                _moveDom.off().remove(); 
                setImgList();
                checkImgCount()
                return false;
            }
            var newImg = _moveDom.clone()
            newImg.data('file',_moveDom.data("file")) 
            newImg.data("url",_moveDom.data("url"))
            if(inX >=$queue.children("li").size())
                newImg.insertBefore($smallBtn); 
            else
                newImg.insertBefore($queue.children("li").eq(inX)); 
            _moveDom.remove()
            $queue.children("li").removeAttr("style") 
            setImgList();
        }
        _move=false;
    })//拖动结束


    function checkImgCount()
    {
        fileCount = $queue.find("img").size(); 
        if(fileCount > 0)
        {
            $placeholder.hide();  
            if(fileCount <  zCount) 
                $smallBtn.show();
            else
                $smallBtn.hide();
        }else
        {
            $placeholder.show();
            $smallBtn.hide();
        }
        uploader.refresh();
    }
    function setImgList()
    {
        var url = new Array();
        if(fileCount > 1 )
        {   
            var size = fileCount

           $queue.children("li").each(function()
            {    
                url.push($(this).data("url"));
            })  
            if(url) url = url.join(",")
        }else
        {
            url = $queue.children("li").data("url"); 
        }
        $upDataList.val(url);
    }


    uploader.on( 'uploadSuccess', function( file,rs ) { 
        var info = $( '#'+file.id ).find('.info');
        info.text(info.attr("title")).removeClass("uping")
        $( '#'+file.id ).data('url',rs.subName) 
        var imgListTemp = '/Uploads/'+rs.subName; 
        imgListTemp = imgListTemp.replace("img/",'thumb/');
        $( '#'+file.id ).children("img").attr("src",imgListTemp)
        setImgList();  
    });
    uploader.on( 'uploadError', function( file,code ) {
        var info = $( '#'+file.id ).find('.info');
        info.text(code)
    })


}

$.fn.checkForm = function(success,inputTips)//检查表单
{  
    // success : 验证成功后的回调函数
    // inputTips ： 验证失败后的处理方法，默认为inputTips方法，可以不填
    var re = true; 
    var form = $(this)
    form.find('.checkForm').blur(function(){  
        if(!$(this).checkFormItem(false,inputTips)) return false
    })
     
    form.off().submit(function(){
        $(this).find('.checkForm').each(function()
        {
            var $_this = $(this)
            re = $_this.checkFormItem(true,inputTips)  
            if(!re) return  re;
        }) 
        if(!re) return  re;
        if(success) return success(form)
    })   
}

$.fn.checkFormItem = function(isSubmit,inputTips)//检查表单合法性
{
    /*
        表单属性   
        data-must = 必填项目
        data-ajax = ajax验证
        data-reg = 正则表达式
        data-regerr = 正则表达式检测错误提示
    */ 
    var _this = $(this); 
    var unique = _this.attr('data-unique');
    var must = _this.attr('data-must');
    var reg = _this.attr('data-reg'); 
    var ajax = _this.attr('data-ajax') 
    var regerr = _this.attr('data-regerr') ? $(this).attr('data-regerr')  : "格式错误！"; 
    var val = _this.val(); 
    var re = true 
    if (must && !val)
    {  
        if(!inputTips) _this.inputTips("不能为空",false,isSubmit)
        else inputTips("不能为空",false,_this)
        return false
    };
    if(reg && val)
    {    
        var regex=eval(reg);   
        if(!regex.test(val) )
        { 
            if(!inputTips) _this.inputTips(regerr,false,isSubmit)
            else inputTips(regerr,false,_this)
            return false;
        }  
    };
    if(ajax && !isSubmit && val)
    {
        ajax = ajax.replace(".html","")
        var name = _this.attr("name")
        ajax = ajax + "/value/" + val + "/type/"+name+".html"
        var ico = _this.parent().next(".tips").children("i.ico")
        ico.addClass("sloading")
        $.getJSON(ajax,function(date){
            ico.removeClass("sloading")
            if(date.status == 1)
            { 
                if(!inputTips) _this.inputTips(date.info,true,isSubmit)
                else inputTips(date.info,true,_this)
            }else
            {
                if(!inputTips) _this.inputTips(date.info,false,isSubmit) 
            }
        })
    } 
    if(!inputTips) _this.inputTips("",true)
    else inputTips("",true,_this)
    return re
}

$.fn.inputTips = function(str,type,isSubmit) //验证完成后反馈方式
{ 
    // str : 提示字符串
    // type ： 判断状态，成功 | 失败
    // isSubmit ：判断是鼠标离开验证还是提交验证
    var _this = $(this)  
    var dom = _this.parents(".form-group")
    var tips = dom.find(".help-block")
    if(!type)
    {  
        dom.addClass("has-error");
        tips.text(str)
        if(isSubmit)
        {
            _this.focus()
        }

    }else
    {
        dom.removeClass("has-error");
        tips.text("")
    }
}

